import mongoose from 'mongoose';
import 'dotenv/config';

const MONGO_DB_URL = 'mongodb://localhost:27017/shareenergy';

const connectToDatabase = (
  mongoDatabaseURI = process.env.MONGO_URI
    || MONGO_DB_URL,
) => mongoose.connect(mongoDatabaseURI);

export default connectToDatabase;

import { Request, Response } from 'express';
import ICustomerService from '../interfaces/ICustumerService';
import { ICustomers } from '../interfaces/ICustomers';

export default class CustomerController {
  constructor(private service: ICustomerService<ICustomers>) { }

  public async create(
    req: Request,
    res: Response,
  ) {
    const { obj } = req.body;
    try {
      await this.service.create(obj);
      return res.status(201).json({ message: 'Usuario criado com sucesso' });
    } catch {
      return res.status(400)
        .json({ message: 'Usuario com os campos j√° cadastrado' });
    }
  }

  public async readOne(
    req: Request,
    res: Response,
  ) {
    const { id } = req.body;
    try {
      const result = await this.service.readOne(id);
      return res.status(201).json(result);
    } catch {
      return res.status(400)
        .json({ message: 'Usuario n√£o encontrado' });
    }
  }

  public async readAll(
    _req: Request,
    res: Response,
  ) {
    try {
      const customers = await this.service.readAll();
      return res.status(200).json(customers);
    } catch {
      return res.status(400)
        .json({ message: 'n√£o foi poss√≠vel encontrar nenhum usuario' });
    }
  }

  public async update(
    req: Request,
    res: Response,
  ) {
    try {
      const { obj } = req.body;
      const { id } = req.params;
      const customers = await this.service.update(id, obj);
      return res.status(204).json(customers);
    } catch {
      return res.status(400)
        .json({ message: 'n√£o foi poss√≠vel atualizar nenhum usuario' });
    }
  }

  public async delete(
    req: Request,
    res: Response,
  ) {
    try {
      const { id } = req.params;
      const customers = await this.service.delete(id);
      return res.status(202).json(customers);
    } catch {
      return res.status(400)
        .json({ message: 'n√£o foi poss√≠vel excluir o cliente' });
    }
  }
}

import ICustomerService from '../interfaces/ICustumerService';
import { ICustomers } from '../interfaces/ICustomers';
import { IModel } from '../interfaces/IModel';

const ErrorMsgNotFound = 'Nenhum usuario encontrado';
const ErrorMsgExist = 'Usuario J√° cadastrado';

class CustomersService implements ICustomerService<ICustomers> { 
  private costumer:IModel<ICustomers>;

  constructor(model:IModel<ICustomers>) {
    this.costumer = model;
  }

  public async create(obj : ICustomers):Promise<ICustomers | Error> {
    const { name, email, cpf } = obj;
    const Users = await this.costumer.readOneCustumer(name, cpf, email);
    if (Users) throw new Error(ErrorMsgExist);
    const result = await this.costumer.create(obj);
    return result;
  }

  public async readOne(_id : string):Promise<ICustomers | undefined> {
    const Users = await this.costumer.readOneId(_id);
    if (!Users) {
      throw new Error(ErrorMsgNotFound);
    }
    return Users;
  }

  public async readAll():Promise<ICustomers[]> {
    const result = await this.costumer.readAll();
    if (!result) throw new Error(ErrorMsgNotFound);
    return result;
  }

  public async update(_id:string, obj:Partial<ICustomers>):Promise<ICustomers> {
    const resultUpdate = await this.costumer.update(_id, obj);
    if (!resultUpdate) throw new Error(ErrorMsgNotFound);
    return resultUpdate;
  }

  public async delete(_id:string):Promise<ICustomers> {
    const resultDelete = await this.costumer.delete(_id);
    if (!resultDelete) throw new Error(ErrorMsgNotFound);
    return resultDelete;
  }
}

export default CustomersService;

